#PRAGMA foreign_keys=OFF;
START TRANSACTION;
DROP TABLE IF EXISTS portfolio;
DROP TABLE IF EXISTS transactions;
DROP TABLE IF EXISTS users;
CREATE TABLE users (id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL, cash NUMERIC NOT NULL DEFAULT 10000.00);
CREATE TABLE transactions (    id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL,    user_id INTEGER NOT NULL,    symbol TEXT NOT NULL,    shares INTEGER NOT NULL,    price NUMERIC NOT NULL,    total_price NUMERIC NOT NULL,    timestamp DATETIME DEFAULT CURRENT_TIMESTAMP, type TEXT,    FOREIGN KEY (user_id) REFERENCES users (id));

CREATE TABLE portfolio (    id INTEGER PRIMARY KEY AUTO_INCREMENT NOT NULL,    user_id INTEGER NOT NULL,    symbol TEXT NOT NULL,    company_name TEXT NOT NULL,    shares INTEGER NOT NULL,    FOREIGN KEY (user_id) REFERENCES users (id));
COMMIT;
